import React from 'react'

const Message = ({message: {user,text},name}) => {
	let isSentByCurrentUser = false, isAdmin=false;
	const trimmedName = name.trim().toLowerCase();
	if(user === trimmedName) isSentByCurrentUser = true;
	if('admin' === trimmedName) isSentByCurrentUser = true;
	if(isSentByCurrentUser){
		return(
			<div className="messageContainer justifyEnd">
				<p className="sentText pr-10">{trimmedName}</p>
				<div className="messageBox backgroundBlue">
					<p className="messageText colorWhite">{text}</p>
				</div>
	        </div>
        );
    }
    else if(isAdmin){
    	return(
			<div className="messageContainer justifyMid">
				<p className="sentText pr-10">{trimmedName}</p>
				<div className="adminMessage">
					<p className="adminText colorLight I">{text}</p>
				</div>
	        </div>
        );
    }
    else {
    	return(
			<div className="messageContainer justifyStart">
				<div className="messageBox backgroundBlue">
					<p className="messageText colorDark">{text}</p>
					<p className="sentText pr-10">{trimmedName}</p>
				</div>
	        </div>
        );
    }
}; 

export default Message
