import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Join = () => {
	const [name, setName] = useState('');
	const [room, setRoom] = useState('');
	let error=false
	const checkEmpty = (event) => {
    	if((!name||!room)){
    		error=true;
    		event.preventDefault();
    		//alert("Username or Room ID is blank");
    	}
  	}
	return(
		<div className="joinOuterContainer">
			<div className="joinInnerContainer">
				<h1 className="heading">Join</h1>
				<div>
					<input placeholder="Name" className="joinInput" type="text" onChange={(event) => setName(event.target.value)} />
				</div>
				<div>
					<input placeholder="Room" className="joinInput" type="text" onChange={(event) => setRoom(event.target.value)} />
				</div>
				<Link onClick={event =>	checkEmpty(event)} to={`/chat?name=${name}&room=${room}`}>
					<button className='button' type="submit">Sign In</button>
				</Link>
				{error &&
                        <div className={'error'}>Something is wrong</div>
                }
			</div>
		</div>
	)
};

export default Join
