import React from 'react'
import ReactEmoji from 'react-emoji'

import '../css/Message.css';

const Message = ({message: {user,text},name}) => {
	let isSentByCurrentUser = false, isAdmin=false;
	const trimmedName = name.trim().toLowerCase();
	if(user === trimmedName) isSentByCurrentUser = true;
	if('admin' === trimmedName) isSentByCurrentUser = true;
	if(isSentByCurrentUser){
		return(
			<div className="messageContainer justifyEnd">
				<p className="sentText pr-10">{trimmedName}</p>
				<div className="messageBox backgroundBlue">
					<p className="messageText colorWhite">{ReactEmoji.emojify(text)}</p>
				</div>
	        </div>
        );
    }
    else if(isAdmin){
    	return(
			<div className="justifyMid">
				<p className="sentText pr-10">Admin</p>
				<div className="adminMessage">
					<p className="adminText colorLight I">{ReactEmoji.emojify(text)}</p>
				</div>
	        </div>
        );
    }
    else {
    	return(
			<div className="messageContainer justifyStart">
				<div className="messageBox backgroundBlue">
					<p className="messageText colorDark">{ReactEmoji.emojify(text)}</p>
					<p className="sentText pr-10">{user}</p>
				</div>
	        </div>
        );
    }
}; 

export default Message
